namespace eMarket\Core;

class View {

    /**
     * Роутинг данных из View
     *
     * @return строка $str
     */
    public function routing() {

        $SET = new \eMarket\Core\Set;

        $str = str_replace('controller', 'view/' . $SET->template(), getenv('SCRIPT_FILENAME'));

        return $str;
    }

}

?>